sp_widget.script:
(function() {
	data.gm_display_enabled = "true";
	data.leaderBoard =[];
	data.loyalty = [];
	var points=[];

	 data.gm_header =  gs.getMessage("Leaderboard ");
		var gr = new GlideRecord('x_236494_titan_enc_loyalty_points_transactions');
		gr.addQuery('latest_transaction','true');
		gr.orderByDesc('points_balance');	
		gr.setLimit(5);
		gr.query();

	while(gr.next()){
		var percent;
				percent = (gr.points_balance*100)/8000;
			console.log('percentage :'+ percent);	

		data.leaderBoard.push({
			firstname: gr.user.first_name.toString(),
			lastname: gr.user.last_name.toString(),
			photo: gr.user.photo.toString(),
			points: gr.points_balance.toString(),
			lev: gr.level.toString(),
			progress: percent

		})
	}




})();
