sp_widget.client_script:
function($rootScope,$scope,$document,spUtil,spModal) {
	/* widget controller */
	var c = this;
	c.showTitanLoader = true;

	c.getProducts = function (subCategory){
		c.data.action="getProducts";
		c.data.selectedSubCategory = subCategory;
		c.server.update().then(function(){
			c.productDetailPage = false;
			c.showModalPhase = false;
			c.showCheckoutPage = false;
			c.showTitanLoader = false;
		});
	};


	$scope.$on("menuSelectedFromRoot", function(event, data){
		c.getProducts(data);
	});

	c.getProductDetails = function(){
		c.productDetailPage = true;
		c.productQuantity = 0;
		c.totalAmount = c.productQuantity*c.data.titanItem.csmdemoofferprice;
		c.redeemPoints = parseInt(c.data.currentPoints);
		c.showTitanLoader = false;
	};

	c.callProductDetailPage = function(sys_id) {
		c.showTitanLoader = true;
		c.showCheckoutPage = false;
		c.showModalPhase = false;
		c.detailPageParameters = {
			'sys_id':sys_id,
			'subCategory':c.selectedSubCategory
		};
		c.data.action="getProductDetail";
		c.data.detailPageParameters = c.detailPageParameters;
		c.server.update().then(c.getProductDetails);
	};

	c.addToCart = function(){
		//spModal.alert('ADD TO CART is not implemented.');
	};

	c.placeOrder = function(item) {
		c.showCheckoutPage = true;
		c.totalAmount = c.productQuantity*c.data.titanItem.csmdemoofferprice;
	};

	c.decrementQuantity = function() {
		c.productQuantity = parseInt(c.productQuantity) - 1;
		c.totalAmount = c.productQuantity*c.data.titanItem.csmdemoofferprice;
	};

	c.incrementQuantity = function() {
		if(parseInt(c.productQuantity) >= 0 ) {
			c.productQuantity = parseInt(c.productQuantity) + 1;
			c.totalAmount = c.productQuantity*c.data.titanItem.csmdemoofferprice;
		}
	};

	c.insertCallback = function() {
		spModal.alert('Your order has been placed successfully.').then(function(){
			c.getProducts(c.data.selectedSubCategory);
		});
	};

	c.insertTransaction=function(){
		console.log("first");
		c.netAmount =(c.productQuantity * parseInt(c.data.titanItem.csmdemoofferprice)) - c.redeemPoints; 
		console.log("second", c.netAmount);
		c.transactionParams={
			'points_redeemed':c.redeemPoints,
			'transaction_amount':c.netAmount,
			'transaction_product':c.data.titanItem.csmdemoproductname,
			'points_earned':Math.round(c.netAmount*0.05,0),
			'remarks':'Reward points',
			'product_sys_id':c.data.titanItem.sys_id
		};
		console.log("third", c.transactionParams);
		c.data.transactionParams=c.transactionParams;
		c.data.action='insertTransaction';
		console.log("fourth", c.data);
		c.server.update().then(c.insertCallback);
	}

$scope.monthList = [];
	for(var j=1;j<=12;j++) {
		$scope.monthList.push(j);
	}

	c.goToPhase2 = function() {
		c.showModalPhase = true;
		var totAmount = (c.productQuantity * parseInt(c.data.titanItem.csmdemoofferprice)) - c.redeemPoints;
		c.emiAmount = Math.round(totAmount/parseInt(c.emimonth));
	}


}

